import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/authContext";
import { Alert } from "./Alert";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser } from "@fortawesome/free-solid-svg-icons";
import { faKey } from "@fortawesome/free-solid-svg-icons";
import { faEarthAmericas } from "@fortawesome/free-solid-svg-icons";
import { faCircleExclamation } from "@fortawesome/free-solid-svg-icons";
import styled from 'styled-components';
import { Overlay } from "react-bootstrap";


export function Login() {
  const [user, setUser] = useState({
    email: "",
    password: "",
  });
  const { login, loginWithGoogle, resetPassword } = useAuth();
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    try {
      await login(user.email, user.password);
      navigate("/");
    } catch (error) {
      setError(error.message);
    }
  };

  const handleChange = ({ target: { value, name } }) =>
    setUser({ ...user, [name]: value });

  const handleGoogleSignin = async () => {
    try {
      await loginWithGoogle();
      navigate("/");
    } catch (error) {
      setError(error.message);
    }
  };

  const handleResetPassword = async (e) => {
    e.preventDefault();
    if (!user.email) return setError("Write an email to reset password");
    try {
      await resetPassword(user.email);
      setError("We sent you an email. Check your inbox");
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="bg-slate-100 w-full max-w-xs m-auto">
      {error && <Alert message={error} />}
      <form
        onSubmit={handleSubmit}
        className="bg-gray shadow-md rounded px-4 pt-3 pb-4 mb-2 shadow rounded border-2"
      >
        <div className="mb-4">
          <label className="block text-black-700 text-sm font-bold mb-2">
            <center>
              <h4>Github Performance</h4>
            </center>
          </label>
          <p></p>
          <label
            htmlFor="email"
            className="block text-gray-700 text-sm font-bold mb-2"
          >
            <FontAwesomeIcon icon={faUser}></FontAwesomeIcon>
            Email
          </label>
          <input
            type="email"
            name="email"
            id="email"
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            placeholder="youremail@gmail.com"
          />
          <p>
          </p>
          <div className="mb-4">
            <label
              htmlFor="password"
              className="block text-gray-700 text-sm font-bold mb-2"
            >
              <FontAwesomeIcon icon={faKey}></FontAwesomeIcon>
              Password
            </label>
            <input
              type="password"
              name="password"
              id="password"
              onChange={handleChange}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="*************"
            />
          </div>
          <div className="flex items-center justify-between">
            <button
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              type="submit"
            >
              Sign In
            </button>
            <a
              className="inline-block align-baseline font-bold text-sm text-blue-500 hover:text-blue-800"
              href="#!"
              onClick={handleResetPassword}
            >
              Forgot Password?
            </a>
            <label>
              <FontAwesomeIcon icon={faCircleExclamation}></FontAwesomeIcon>
              </label>
          </div>
          <p>
          </p>
          <button
            onClick={handleGoogleSignin}
            className="bg-slate-50 hover:bg-slate-200 text-black  shadow rounded border-2 border-gray-300 py-2 px-4 w-full"
          >
            <FontAwesomeIcon icon={faEarthAmericas}></FontAwesomeIcon>
            Google login
          </button>
          <label>
          <FontAwesomeIcon icon={faCircleExclamation}></FontAwesomeIcon>
          </label>
          <p className="my-4 text-sm flex justify-between px-3">
            Don't have an account?
            <Link to="/register" className="text-blue-700 hover:text-blue-900">
              Register
            </Link>
          </p>
        </div>
      </form>
    </div>
  );
}

<div>
        <>
        <Overlay>
          <ContenedorModal>
            <EncabezadoModal>
              <h3>Titulo</h3>
            </EncabezadoModal>
            <BotonCerrar>X</BotonCerrar>
          </ContenedorModal>
        </Overlay>
        </>
		</div>


Overlay = styled.div`
  width: 100vw;
  height: 100vh;
  position: fired;
  top: 0;
  left: 0;
  background: rgba(0, 0, 0, 0.5);
  padding: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ContenedorModal = styled.div`
  width: 500px;
  min-height: 100px;
  background: #fff;
  position: relative;
  border-radius: 5px;
  box-shadow: rgba(100, 100, 111, 0.2) 0px 7px 29px 0px;
  padding: 20px;
`;

const EncabezadoModal = styled.div`
display: flex;
align-items: center;
justify-content: space-between;
margin-bottom: 20px;
padding-bottom: 20px;
border-bottom: 1px solid #E8E8E8;

h3{
  font-weight: 500;
  font-size: 16px;
  color: #1766DC;
}
`

const BotonCerrar = styled.div`
position: absolute;
top: 20px;
rigth: 20px;
width: 30px;
height: 30px;
border: none;
background: none;
curson: pointer;
transition: .3s ease all;
border-radius: 5px;
color: #1766DC;
`
